mapper.select:
SELECT u.username, u.`name`, u.home_url, u.photo_url, b.blog_url, b.title, b.content, b.published_date, b.read_num, b.comment_num, bt.* FROM blogs b LEFT JOIN users u ON b.uid = u.uid LEFT JOIN blog_type bt ON bt.btid = b.btid WHERE b.bid>=( SELECT floor(RAND()*(( SELECT MAX(bid) FROM blogs)-( SELECT MIN(bid) FROM blogs))+( SELECT MIN(bid) FROM blogs)))
and bt.alias=#{blogsVo.alias}
ORDER BY
b.bid
b.published_date desc
LIMIT 50;

SELECT u.username, u.home_url, u.photo_url, a.*, am.ask_money, GROUP_CONCAT( askt.asktid ) asktids, GROUP_CONCAT( askt.ask_type ) ask_types FROM ask a LEFT JOIN ask_money am ON a.amid = am.amid LEFT JOIN users u ON u.uid = a.uid LEFT JOIN ask_type askt ON askt.aid = a.aid AND askt.uid = u.uid
and a.answer_num=#{answer_num}
and a.accept_status=#{accept_status}
GROUP BY a.aid ORDER BY ${rules} desc
